INSERT INTO `products` VALUES (46, 'Asus Vivobook A412FA i3 8145U/4GB/512GB', 'asus-vivobook-a412fa-i3-8145u4gb512gb', 'frontend/img/product/1662811026_A412FA-4.jpg', 3, 7890000, 6900000, 0, 1, 0, 0, 4, NULL, 'EK661T', NULL, NULL, 12, 4, '<p><img alt=\"\" src=\"data:image/png;base64,\" /></p>', '<div class=\"content-article\">\r\n<h2><a href=\"https://www.thegioididong.com/laptop/asus-vivobook-s412f-i3-8145u-4gb-512gb-ek342t\" target=\"_blank\" title=\"Laptop Asus Vivobook A412F i3 8145U chính hãng đang kinh doanh tại Thegioididong\" type=\"Laptop Asus Vivobook A412F i3 8145U chính hãng đang kinh doanh tại Thegioididong\">Asus Vivobook </a><a href=\"https://www.thegioididong.com/laptop/asus-vivobook-s412f-i3-8145u-4gb-512gb-ek342t#1\" target=\"_blank\" title=\"Laptop Asus Vivobook S412F i3 8145U chính hãng đang kinh doanh tại Thegioididong\" type=\"Laptop Asus Vivobook S412F i3 8145U chính hãng đang kinh doanh tại Thegioididong\">A412F i3 8145U (EK342T)</a>&nbsp;sở hữu h&agrave;ng loạt c&aacute;c t&iacute;nh năng hiện đại như bảo mật v&acirc;n tay, bản lề ErgoLift tự n&acirc;ng ph&iacute;m th&ocirc;ng minh. Đ&acirc;y l&agrave; một trong những chiếc <a href=\"https://www.thegioididong.com/laptop?g=mong-nhe-thoi-trang\" target=\"_blank\" title=\"Laptop nhỏ gọn chính hãng, giá rẻ bán tại Thế Giới Di Động \" type=\"Laptop nhỏ gọn chính hãng, giá rẻ bán tại Thế Giới Di Động \">laptop nhỏ gọn</a> ph&ugrave; hợp với đối tượng sinh vi&ecirc;n, nh&acirc;n vi&ecirc;n văn ph&ograve;ng cần một chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu máy tính xách tay đang bán tại thegioididong.com\" type=\"Xem thêm các mẫu máy tính xách tay đang bán tại thegioididong.com\">m&aacute;y t&iacute;nh x&aacute;ch tay</a>&nbsp;xử l&yacute; tốt c&aacute;c t&aacute;c vụ h&agrave;ng ng&agrave;y.</h2>\r\n\r\n<h3>Thiết kế gọn nhẹ, bản lề ErgoLift hiện đại</h3>\r\n\r\n<p>Laptop&nbsp;<a href=\"https://www.thegioididong.com/laptop-asus\" target=\"