INSERT INTO `products` VALUES (69, 'Thẻ nhớ MicroSD 32 GB samsung', 'the-nho-microsd-32-gb-samsung', 'frontend/img/product/1662466511_the-nho-32gb.png', 5, 200000, 30000, 3, 1, 1, 0, 3, '.thegioididong.com.', NULL, NULL, NULL, 5, 4, '<div class=\"margin-0 padding-0 product-summary product_description\">\r\n<div class=\"description rte rte-summary\">\r\n<ul>\r\n	<li>Loại sản phẩm: microSD</li>\r\n	<li>Dung lượng: 32 GB</li>\r\n	<li>Tốc độ đọc: 95 MB/s</li>\r\n	<li>Tốc độ Ghi: 20 MB/s</li>\r\n	<li>Bảo h&agrave;nh: 5 năm</li>\r\n</ul>\r\n</div>\r\n</div>', '<div class=\"ba-text-fpt\">\r\n<p><img src=\"https://bizweb.sapocdn.net/100/329/122/files/evoplus2017-banner1.jpg?v=1577952106725\" style=\"width:100%\" /></p>\r\n\r\n<table border=\"0\" cellpadding=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:left\">\r\n			<p><span style=\"font-size:18pt\"><strong><a href=\"https://www.memoryzone.com.vn/the-nho-microsdhc-samsung-evo-plus-u1-32gb-new-2017\"><span style=\"color:#000000\">Thẻ nhớ MicroSD Samsung Evo Plus U1 95MB/s</span></a></strong></span></p>\r\n\r\n			<p><span style=\"color:#000000\">Thẻ nhớ MicroSD Samsung Evo Plus U1 95MB/s - Bộ nhớ nhanh nhất thế giới m&agrave; Samsung vừa cho ra đời gi&uacute;p đ&aacute;p ứng tất cả nhu cầu về tốc độ, kh&ocirc;ng gian lưu trữ v&agrave; cải thiện tối đa hiệu năng của những d&ograve;ng SmartPhone v&agrave; Tablet mới nhất. Cho bạn sự trải nghiệm tuyệt vời nhất. Thẻ nhớ MicroSD Samsung Evo Plus U1 95MB/sc&oacute; tốc độ truy xuất l&ecirc;n đến 95MB/s đạt chuẩn UHS-1 U1 cho việc truyền tải nhạc, h&igrave;nh ảnh v&agrave; ghi video 4K, Full HD. Thẻ c&ograve;n c&oacute; chức năng chống thấm nước, chịu được ch&ecirc;ch lệch nhiệt độ cao, kh&ocirc;ng bị ảnh hưởng bởi từ t&iacute;nh v&agrave; tia X, do đ&oacute;, dữ liệu của bạn sẽ lu&ocirc;n được bảo vệ an to&agrave;n trong mọi trường hợp.</span></p>\r\n\r\n			<p><span style=\"color:#ff0000\">***** Adapter MicroSD to SD đi k&egrave;m c&oacute; thể chịu sự thay đổi kh&ocirc;ng b&aacute;o trước từ nh&agrave; sản xuất.</span></p>\r\n			</td>\r\n			<td><img src=\"https://bizweb.sapocdn.net/100/329/122/files/evoplus2017-01.jpg?v=1577936571761\" style=\"width:100%\" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border=\"0\" cellpadding=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img src=\"https://bizweb.sapocdn.net/100/329/122/files/evoplus2017-02.jpg?v=1577936587634\" style=\"width:100%\" /></td>\r\n			<td style=\"text-align:left\">\r\n			<p><span style=\"color:#000000\"><span style=\"font-size:18pt\"><strong>Tốc độ cực cao đ&aacute;p ứng Video Full HD chất lượng cao</strong></span></span></p>\r\n\r\n			<p><span style=\"color:#000000\">Thẻ nhớ MicroSD Samsung Evo Plus U1 95MB/s với tốc độ si&ecirc;u cao l&ecirc;n đến 95MB/s đọc v&agrave; 20MB/s ghi gi&uacute;p bạn chụp ảnh h&agrave;nh động, thể thao si&ecirc;u mượt, rất l&yacute; tưởng cho quay Video Full HD. Cho ph&eacute;p bạn di chuyển c&aacute;c tập tin cực nhanh v&agrave; đảm bảo rằng Video của bạn sẽ kh&ocirc;ng bị mất bất k&igrave; khung h&igrave;nh n&agrave;o.</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border=\"0\" cellpadding=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:left\">\r\n			<p><span style=\"color:#000000\"><span style=\"font-size:18pt\"><strong>Dung lượng cực lớn</strong></span></span></p>\r\n\r\n			<p><span style=\"color:#000000\">Thẻ nhớ MicroSD Samsung Evo Plus U1 95MB/s c&oacute; dung lượng từ 32GB đến 256GB, Đ&aacute;p ứng mọi nhu cầu lưu trữ của bạn.</span></p>\r\n			</td>\r\n			<td><img src=\"https://bizweb.sapocdn.net/100/329/122/files/evoplus2017-03.jpg?v=1577936599683\" style=\"width:100%\" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border=\"0\" cellpadding=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img src=\"https://bizweb.sapocdn.net/100/329/122/files/evoplus80-04.jpg?v=1577936629992\" style=\"width:100%\" /></td>\r\n			<td style=\"text-align:left\">\r\n			<p><span style=\"color:#000000\"><span style=\"font-size:18pt\"><strong>Kh&ocirc;ng thấm nước</strong></span></span></p>\r\n\r\n			<p><span style=\"color:#000000\">Thẻ nhớ MicroSD Samsung Evo Plus U1 95MB/s c&oacute; thể chịu đựng&nbsp;tới 72 giờ ng&acirc;m trong nước biển.</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border=\"0\" cellpadding=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:left\">\r\n			<p><span style=\"color:#000000\"><span style=\"font-size:18pt\"><strong>Chịu nhiệt si&ecirc;u tốt</strong></span></span></p>\r\n\r\n			<p><span style=\"color:#000000\">Thẻ nhớ MicroSD Samsung Evo Plus U1 95MB/sc&oacute; thể chịu đựng được nhiệt độ từ -25&deg; đến 85&deg; C.</span></p>\r\n			</td>\r\n			<td><img src=\"https://bizweb.sapocdn.net/100/329/122/files/evoplus80-05.jpg?v=1577936638605\" style=\"width:100%\" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border=\"0\" cellpadding=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img src=\"https://bizweb.sapocdn.net/100/329/122/files/evoplus80-06.jpg?v=1577936646553\" style=\"width:100%\" /></td>\r\n			<td style=\"text-align:left\">\r\n			<p><span style=\"color:#000000\"><span style=\"font-size:18pt\"><strong>Chống tia X-Ray</strong></span></span></p>\r\n\r\n			<p><span style=\"color:#000000\">Thẻ nhớ MicroSD Samsung Evo Plus U1 95MB/s gi&uacute;p bảo vệ tất cả dữ liệu của bạn khỏi bị hư hại g&acirc;y ra bởi c&aacute;c m&aacute;y qu&eacute;t X-ray trong s&acirc;n bay.</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border=\"0\" cellpadding=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:left\">\r\n			<p><span style=\"color:#000000\"><span style=\"font-size:18pt\"><strong>Chống từ t&iacute;nh</strong></span></span></p>\r\n\r\n			<p><span style=\"color:#000000\">Thẻ nhớ MicroSD Samsung Evo Plus U1 95MB/s c&oacute; thể chống từ trường l&ecirc;n đến 15.000 gauss, tương đương với một m&aacute;y qu&eacute;t High-Field MRI.</span></p>\r\n			</td>\r\n			<td><img src=\"https://bizweb.sapocdn.net/100/329/122/files/evoplus80-07.jpg?v=1577936657642\" style=\"width:100%\" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border=\"0\" cellpadding=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img src=\"https://bizweb.sapocdn.net/100/329/122/files/evoplus2017-04.jpg?v=1577936669735\" style=\"width:100%\" /></td>\r\n			<td style=\"text-align:left\">\r\n			<p><span style=\"color:#000000\"><span style=\"font-size:18pt\"><strong>Tương th&iacute;ch cực tốt</strong></span></span></p>\r\n\r\n			<p><span style=\"color:#000000\">Thẻ nhớ MicroSD Samsung Evo Plus U1 95MB/s tương th&iacute;ch với mọi thiết bị như&nbsp;điện thoại th&ocirc;ng minh, m&aacute;y t&iacute;nh bảng v&agrave; m&aacute;y ảnh. Thẻ nhớ n&agrave;y hoạt động ho&agrave;n hảo với ngay cả những thiết bị ti&ecirc;n tiến nhất để lưu giữ v&agrave; bảo vệ những kỷ niệm của bạn.</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align:center\"><iframe frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/Chm_3p5Myv4\" width=\"560\"></iframe></p>\r\n</div>', NULL, NULL, 3, '2022-06-13 16:10:50', '2022-09-06 12:15:11', NULL);
INSERT INTO `products` VALUES (70, 'Thẻ nhớ MicroSD 128 GB samsung', 'the-nho-microsd-128-gb-samsung', 'frontend/img/product/1662466275_the-nho-microsd-128gb-samsung-pro-plus-1-420x420.jpg', 10, 500000, 5000, 3, 1, 1, 0, 3, '.2.', NULL, NULL, NULL, 5, 4, '<p><img src=\"data:image/png;base64,\" /></p>', '<h2>Giới thiệu sản phẩm Thẻ nhớ MicroSD 128GB Samsung PRO Plus</h2>\r\n\r\n<div class=\"data_description product_description product_description_full\">\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"lazy\" src=\"https://tuanphong.vn/upload_images/images/2021/09/The-nho-MicroSD-Samsung-PRO-Plus-4-.jpg\" /></p>\r\n\r\n<h3>Tất cả kh&ocirc;ng gian bạn cần</h3>\r\n\r\n<p>Tốc độ n&acirc;ng cao + khả năng tương th&iacute;ch = hiệu suất đ&aacute;ng tin cậy. Thẻ nhớ MicroSD Samsung PRO Plus&nbsp;đ&aacute;p ứng tất cả nhu cầu về tốc độ, kh&ocirc;ng gian lưu trữ v&agrave; cải thiện tối đa hiệu năng của những d&ograve;ng SmartPhone v&agrave; Tablet mới nhất.&nbsp;Thẻ nhớ MicroSD Samsung PRO Plus cho ph&eacute;p bạn lưu trữ rất nhiều phương tiện tr&ecirc;n điện thoại, tải tr&ograve; chơi v&agrave;o bảng điều khiển của bạn hoặc tải xuống nhiều ứng dụng hơn tr&ecirc;n m&aacute;y t&iacute;nh bảng của bạn.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"lazy\" src=\"https://tuanphong.vn/upload_images/images/2021/09/The-nho-MicroSD-Samsung-PRO-Plus-1-.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<h3>Nhanh ch&oacute;ng v&agrave; mượt m&agrave;</h3>\r\n\r\n<p>Thẻ nhớ MicroSD Samsung PRO Plus với tốc độ si&ecirc;u cao l&ecirc;n đến 130 MB/s đọc gi&uacute;p bạn chụp ảnh h&agrave;nh động, thể thao si&ecirc;u mượt, rất l&yacute; tưởng cho quay Video Full HD. Cho ph&eacute;p bạn di chuyển c&aacute;c tập tin cực nhanh v&agrave; đảm bảo rằng Video của bạn sẽ kh&ocirc;ng bị mất bất k&igrave; khung h&igrave;nh n&agrave;o.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"lazy\" src=\"https://tuanphong.vn/upload_images/images/2021/09/The-nho-MicroSD-Samsung-PRO-Plus-2-.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<h3>Dung lượng cực lớn</h3>\r\n\r\n<p>Thẻ nhớ MicroSD Samsung Evo Plus&nbsp;MB-MC c&oacute; dung lượng từ 32GB đến 512GB, Đ&aacute;p ứng mọi nhu cầu lưu trữ của bạn.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"lazy\" src=\"https://tuanphong.vn/upload_images/images/2021/09/The-nho-MicroSD-Samsung-PRO-Plus-3-.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<h3>Bạn được bảo vệ ho&agrave;n to&agrave;n</h3>\r\n\r\n<p>Thẻ nhớ MicroSD Samsung PRO Plus c&oacute; thể chịu đựng&nbsp;tới 72 giờ ng&acirc;m trong nước biển, chịu đựng được nhiệt độ từ -25&deg; đến 85&deg; C, bảo vệ tất cả dữ liệu của bạn khỏi bị hư hại g&acirc;y ra bởi c&aacute;c m&aacute;y qu&eacute;t X-ray trong s&acirc;n bay,&nbsp;chống từ trường l&ecirc;n đến 15.000 gauss, tương đương với một m&aacute;y qu&eacute;t High-Field MRI.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"lazy\" src=\"https://tuanphong.vn/upload_images/images/2021/09/The-nho-MicroSD-Samsung-PRO-Plus-5-.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<h3>Thương hiệu bộ nhớ flash số 1 thế giới</h3>\r\n\r\n<p>Hiệu suất v&agrave; độ tin cậy m&agrave; bạn c&oacute; thể tin tưởng từ thương hiệu số một thế giới về bộ nhớ flash kể từ năm 2003. Tất cả phần sụn v&agrave; th&agrave;nh phần đều được sản xuất trong nh&agrave;, bao gồm cả DRAM v&agrave; NAND nổi tiếng thế giới của ch&uacute;ng t&ocirc;i, cho chất lượng m&agrave; bạn c&oacute; thể tin tưởng.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"lazy\" src=\"https://tuanphong.vn/upload_images/images/2021/09/The-nho-MicroSD-Samsung-PRO-Plus-6-.jpg\" style=\"height:407px; width:640px\" /></p>\r\n\r\n<h2>Video sản phẩm Thẻ nhớ MicroSD 128GB Samsung PRO Plus</h2>\r\n\r\n<div class=\"detail_product_video\"><iframe class=\"lazy\" frameborder=\"0\" height=\"450\" src=\"https://www.youtube.com/embed/F5xHbmx8Xio?rel=0&amp;amp;showinfo=0\" style=\"height: 450px;\" title=\"Video sản phẩm Thẻ nhớ MicroSD 128GB Samsung PRO Plus\" width=\"800\"></iframe></div>\r\n</div>', NULL, NULL, 3, '2022-06-13 16:14:11', '2022-09-06 20:24:14', NULL);
